import { React, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { deleteCard, get } from "../services/cardService";

const Card = ({ card, handleClick }) => {
  const [cards, setCards] = useState([]);
  useEffect(() => {
    if (localStorage.getItem("token"))
      getMeCards(localStorage.getItem("token"), (data) => {
        setCards(data);
      });
  }, []);

  return (
    <div className="col-md-6 col-lg-4 mt-3">
      <div className="card border border-dark">
        <img
          className="p-2 rounded mx-auto d-block"
          src={card.bizImage}
          width="250"
          height="200"
          alt={card.bizName}
        />
        <div className="card-body">
          <h5 className="card-title">{card.bizName}</h5>
          <p className="card-text">{card.bizDescription}</p>
          <p className="card-text border-top border-dark pt-2">
            <b>Tel: </b>
            <a href="tel:{card.bizPhone}">{card.bizPhone}</a>
            <br />
            {card.bizAddress}
          </p>
          <Link to={`/my-cards/edit/${card._id}`}>Edit</Link> |
          <button
            className="ml-2"
            // to={`/my-cards/delete/${card._id}`}
            onClick={(e) => {
              e.preventDefault();
              handleClick(card._id);
            }}
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default Card;
