import http from "./httpService";
import { apiUrl } from "../config.json";

let baseUrl= "http://localhost:3000";

export function getCard(cardId) {
  return http.get(`${apiUrl}/cards/${cardId}`);
}

export function editCard(card) {
  const cardId = card._id;
  delete card._id;
  return http.put(`${apiUrl}/cards/${cardId}`, card);
}

export function getMyCards() {
  return http.get(`${apiUrl}/cards/my-cards`);
}

export function createCard(card) {
  return http.post(`${apiUrl}/cards`, card);
}

export function  deleteCard(idToDelete, token, callback) {
  let url =baseUrl+"/api/cards/"+idToDelete;
  fetch(url, {method:'DELETE', headers:{'x-auth-token':token}})
  .then(x=>x.json())
  .then(x=> callback(x))
  .catch(x=>callback(x));
}

export default {
  createCard,
  getMyCards,
  editCard,
  getCard
};
